async function translateAllRemaining() {
  try {
    console.log('🚀 Traduciendo TODOS los verbos restantes...\n');
    
    let updated = 0;
    let errors = 0;
    const total = Object.keys(verbDict).length;
    
    for (const [infinitive, data] of Object.entries(verbDict)) {
      try {
        const verb = await prisma.verb.findFirst({
          where: { infinitive }
        });
        
        if (verb) {
          await prisma.verb.update({
            where: { id: verb.id },
            data: {
              spanishTranslation: data.spanish,
              examples: data.examples,
              spanishExamples: data.spanishExamples
            }
          });
          
          updated++;
          if (updated % 50 === 0) {
            console.log(`✅ Progreso: ${updated}/${total} verbos completados`);
          }
        }
      } catch (error) {
        errors++;
        console.error(`❌ Error con "${infinitive}":`, error);
      }
    }
    
    console.log(`\n🎉 ¡COMPLETADO! ${updated} verbos traducidos (${errors} errores)`);
    console.log(`\nAhora todos los 996 verbos están completamente traducidos al español con ejemplos de calidad.`);
    
  } catch (error) {
    console.error('Error general:', error);
  } finally {
    await prisma.$disconnect();
  }
}

translateAllRemaining();
